    public static void setup() throws Exception {
        if (!RadioList.f.exists()) {
            for (int i = 0; i < RadioList.urls.length; ++i) {
                addSender(RadioList.names[i], RadioList.urls[i]);
            }
        }
        else {
            final FileInputStream file = new FileInputStream(RadioList.f);
            final ByteArrayOutputStream b = new ByteArrayOutputStream();
            final byte[] c = new byte[2048];
            int j = -1;
            while ((j = file.read(c)) > 0) {
                b.write(c, 0, j);
            }
            file.close();
            final String t = new String(b.toByteArray());
            RadioList.jsonObject = JSONObject.parse(t);
            final JSONArray pointer = (JSONArray)RadioList.jsonObject.get("radiolist");
            for (int d = 0; d < pointer.size(); d += 2) {
                final String sender = pointer.getString(d);
                final String url = pointer.getString(d + 1);
                addSender(sender, url);
            }
        }
    }
    
    public static void addSender(final String sender, final String url) {
        RadioList.radioSender.add(sender);
        RadioList.radioUrl.add(url);
    }
    
    public static void removeSender(final String sender, final String url) {
        RadioList.radioSender.remove(sender);
        RadioList.radioUrl.remove(url);
    }
    
    public static void disable() throws Exception {
        final JSONArray jsonArray = new JSONArray();
        for (int i = 0; i < RadioList.radioSender.size() * 2; i += 2) {
            jsonArray.setString(i, RadioList.radioSender.get(i / 2));
            jsonArray.setString(i + 1, RadioList.radioUrl.get(i / 2));
        }
        if (RadioList.jsonObject == null) {
            RadioList.jsonObject = new JSONObject();
        }
        RadioList.jsonObject.setJSONArray("radiolist", jsonArray);
        save();
    }
    
    private static void save() throws Exception {
        if (!RadioList.f.exists()) {
            RadioList.f.createNewFile();
        }
        final byte[] s = RadioList.jsonObject.toString().getBytes();
        final FileOutputStream file = new FileOutputStream(RadioList.f);
        file.write(s);
        file.flush();
        file.close();
    }
